CREATE TABLE IF NOT EXISTS `bureau_login` (
  `date_modified` timestamp NOT NULL default CURRENT_TIMESTAMP,
  `date_created` timestamp NOT NULL default '0000-00-00 00:00:00',
  `active_status` enum('active','inactive') NOT NULL default 'active',
  `company_id` int(10) unsigned NOT NULL default '0',
  `bureau_id` int(10) unsigned NOT NULL default '0',
  `account_live` varchar(32) NOT NULL default '',
  `subaccount_live` varchar(32) default NULL,
  `password_live` varchar(32) NOT NULL default '',
  `account_test` varchar(32) default NULL,
  `subaccount_test` varchar(32) default NULL,
  `password_test` varchar(32) default NULL,
  PRIMARY KEY  (`company_id`,`bureau_id`)
);
